import React from "react";
import { Route, Routes } from "react-router-dom";

import Apps from "./Apps";
import Funds from "./Funds";
import Holdings from "./Holdings";

import Orders from "./Orders";
import Positions from "./Positions";
import Summary from "./Summary";
import WatchList from "./WatchList";
import BuyActionWindow from "./BuyActionWindow";
import SignupForm from "./SignupForm";
import LoginForm from "./LoginForm";
// import AccountDropdown from "./AccountDropdown";
import { AuthModalProvider, useAuthModal } from "./AuthModalContext";
import { GeneralContextProvider } from "./GenralContext";

const Dashboard = () => {
  const { modalType, closeModal } = useAuthModal();
  return (
    <div className="dashboard-container">
      <div className="content">
        <Routes>
          <Route exact path="/" element={<Summary />} />
          <Route path="/orders" element={<Orders />} />
          <Route path="/holdings" element={<Holdings />} />
          <Route path="/positions" element={<Positions />} />
          <Route path="/funds" element={<Funds />} />
          <Route path="/apps" element={<Apps />} />
          {/* <Route path="/signup" element={<SignupForm />} />
          <Route path="/login" element={<LoginForm />} /> */}
        </Routes>
      </div>
      {/* Only show watchlist if no modal is open */}
      {modalType === "" && <WatchList />}

      {/* Modal Overlay */}
      {modalType && (
        <div className="overlay" onClick={closeModal}>
          <div className="modal" onClick={(e) => e.stopPropagation()}>
            {modalType === "login" ? <LoginForm /> : <SignupForm />}
          </div>
        </div>
      )}
      {/* <BuyActionWindow /> */}
      {/* <GeneralContextProvider>
        <WatchList />
      </GeneralContextProvider> */}
    </div>
  );
};

export default Dashboard;
