import React, { useState } from "react";

import "./BuyActionWindow.css";
import { Link } from "react-router-dom";
import axios from "axios";
import { Mode } from "@mui/icons-material";
import GeneralContext from "./GenralContext";

function BuyActionWindow({ uid }) {
  let [stockQuantity, setStockQuantity] = useState(1);
  let [stockPrice, setStockPrice] = useState(0.0);

  const handleBuyClick = () => {
    axios.get("http://localhost:3000/newOrder", {
      name: uid,
      qty: stockQuantity,
      price: stockPrice,
      mode: "BUY",
    });
    GeneralContext.closeBuyWindow();
  };
  const handleCancelClick = () => {
    GeneralContext.closeBuyWindow();
  };

  return (
    <div className="container" id="buy-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <filedset>
            <legend>Qty.</legend>
            <input
              type="number"
              placeholder="qty"
              name="qty"
              id="qty"
              onChange={(e) => setStockQuantity(e.target.value)}
              value={stockQuantity}
            />
          </filedset>
          <filedset>
            <legend>Price</legend>
            <input
              type="number"
              name="price"
              placeholder="price"
              id="price"
              step="0.05"
              onChange={(e) => setStockPrice(e.target.value)}
              value={stockPrice}
            />
          </filedset>
        </div>
      </div>
      <div className="buttons">
        <span>Margin required Rs140.65</span>
        <div className="btns">
          <Link className="btn btn-blue" onChange={handleBuyClick}>
            Buy
          </Link>
          <Link className="btn btn-grey" onChange={handleCancelClick}>
            Cancel
          </Link>
        </div>
      </div>
    </div>
  );
}

export default BuyActionWindow;
