  return (
    <div className="container" id="buy-window" draggable="true">
      <div className="regular-order">
        <div className="inputs">
          <filedset>
            <legend>Qty.</legend>
            <input type="number" placeholder="qty" name="qty" id="qty" />
          </filedset>
          <filedset>
            <legend>Price</legend>
            <input
              type="number"
              name="price"
              placeholder="price"
              id="price"
              step="0.05"
            />
          </filedset>
        </div>
      </div>
      <div className="buttons">
        <span>Margin required Rs140.65</span>
        <div className="btns">
          <Link className="btn btn-blue">Buy</Link>
          <Link className="btn btn-grey">Cancel</Link>
        </div>
      </div>
    </div>
  );
}

export default BuyActionWindow;
